"use strict";$(function(){$(window).scroll(function(){if($(window).scrollTop()+$(window).height()>=$(".containerbottom").offset().top+$(".containerbottom").height()){if(XIAO.loadingArticle==0&&XIAO.getMoreUrl!=""){XIAO.loadingArticle=1;var loArtcleNumber=XIAO.loadedCount;$.ajax({type:"POST",url:XIAO.getMoreUrl,data:{articleOffset:loArtcleNumber},success:function(datas){if(datas.length!=0){XIAO.loadedCount=parseInt(loArtcleNumber)+datas.length;$.each(datas,function(i,data){var loInsertHtml=getLoadingPage(data);$("#home_articles").append(loInsertHtml);});}XIAO.loadingArticle=0;},dataType:"json"});}}});});String.prototype.template=function(obj){return this.replace(/\$\w+\$/gi,function(matchs){var returns=obj[matchs.replace(/\$/g,"")];return(returns+"")=="undefined"?"":returns;});};function getLoadingPage(data){data.points=data.up-data.down;var insertTempl=$("#section_template").val();insertTempl=insertTempl.template(data);return insertTempl;}function articlePointUp(target,id){if($("#up"+id).hasClass("up_c")){return false;}$.ajax({url:ROOT_PATH+"/article/articlePointUp",type:"GET",data:{id:id},dataType:"json",success:function(data){if(data.state==1){var val=$("#rpoints"+id).html();$("#rpoints"+id).html(parseInt(val)+1);$("#up"+id).addClass("up_c");$("#down"+id).removeClass("down_c");}else{alert("评分失败，请反馈给管理员！");}},error:function(data){alert("评分失败，请反馈给管理员！");}});}function articlePointDown(target,id){if($("#down"+id).hasClass("down_c")){return false;}$.ajax({url:ROOT_PATH+"/article/articlePointDown",type:"GET",data:{id:id},dataType:"json",success:function(data){if(data.state==1){var val=$("#rpoints"+id).html();$("#rpoints"+id).html(parseInt(val)-1);$("#down"+id).addClass("down_c");$("#up"+id).removeClass("up_c");}else{alert("评分失败，请反馈给管理员！");}},error:function(data){alert("评分失败，请反馈给管理员！");}});}